style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("haskell.org") {
    body, .toc, .inline-code, #footer, #column-one, .catlinks, #content, #globalWrapper #content .source-haskell .st0,
    .home-dynamic { 
        background-color: #333;
        color: white;
    }
    h1, h2, h3, h4, h5, h6, code {
        color: white;
    }
    a:link
    {
        color:#48f;
    }
    a:visited
    {
        color:#7cf;
    }
    pre, .src {
        background:#202428!important;
        border-color:#101418;
    }
    #interface p.src .link { background:#111;}
}
