style.css:
@-moz-document domain("play.spotify.com") {
#main>div.stacked>div.root.covered,
#main>div.stacked>div.back {
    display: none;
}

#main>div.stacked>div.front {
    width: 100%;
    padding-left: 0px;
}

@media screen and (max-width: 1080px) {
    #wrapper {
        min-width: inherit;
    }
}

html,
body.non-mobile,
body.non-mobile .container {
    min-width: inherit;
}
}
