style.css:
@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
/*borders*/
.post_content_inner.clearfix{padding-left: 12px!important;padding-right:12px!important;}
.post.post_full {width: 564px!important;} 
#posts.posts {margin: 0px 0px 0px 86px!important;}
.post.post_full .post_header, .post.post_full .post_footer, .post.post_full .post_answer {padding-right: 12px;padding-left: 12px;}
.post.post_full .post_body, .post.post_full .post_title {padding-right: 0px;padding-left: 0px;}
.post_full.is_note .post_body .note_item, .post-composer_note-post .note_item {width: 480px!important;}
.notification {width: 564px!important;}
.post.post_full .post_tags{padding-left:12px;padding-right:12px;}
.post .post_tags.draggable, .post .post_tags.draggable .post_tags_inner {margin: 0 12px 0 0;}
.post_full .post_media_small {margin: 13px 0px 10px;}

/*padding and margin and alignments*/
#right_column.right_column{padding-top:10px!important;}
.identity-refresh .l-container {padding-top: 62px!important;}
#dashboard_controls {font-size: 16px!important;line-height: 1;height: 12px;padding-top: 10px;margin-bottom: 10px;}#pagination {margin-top: 0;padding-bottom: 13px;margin-right: 12px;}
.identity-refresh .post_avatar {margin-left: 10px;}#posts.posts>.post_container {margin: 0 0 20px -10px;}
.identity-refresh .l-container .l-content {padding: 8px;}
.pagination {padding-left: 75px!important;width: 565px!important;}

/*header*/
.l-header-container--refresh {box-shadow: none!important;background:none!important;position:absolute!important;;width: 100%;height:52px!important;}
.l-header {position:absolute;width:960px!important;max-width:960px!important;padding: 0 35px 0 35px!important;}
.compose-button{display:none!important;}

/*red notifications*/
.identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {border: 0px;}
.identity-refresh .selected .tab_notice {background:#D95E40!important;}
.identity-refresh .tab_notice {background: #d95e40!important;}
.l-header-container--refresh .tab_notice {padding: 0 3px;border-radius: 2px;height: 18px;min-width:18px;font-size: 12px;}
.identity-refresh .tab_notice .tab_notice_value {color: #fff!important;}
.identity-refresh .inbox_notice_container {background:#D95E40!important;}

/*ask post borders on asks and replies*/
  .post_full.is_note .post_body .note_item {background-color: #F2F2F2;border: 1px solid #CCCCCC!important;}
  .post_full.is_note .nipple, .post-composer_note-post .nipple {border-left: 8px solid #CCC!important;right: -10px!important;}


.post-container {width: 540px;min-height: 50px;padding:10px!important;}
.post-form--form.post-content{max-width:540px;padding:0px;}
.post-form--controls .controls-container {max-width: 540px!important;padding: 8px 0 0 0;}
.post-form--header .controls-container {display: table;max-width: 540px!important;padding:0px 0px 10px 0px!important;}
.post-form--tag-editor {padding:0px;overflow: hidden;word-break: break-all;}

.caption-field .editor-richtext {line-height: 21px;word-wrap: break-word;width: 540px!important;}
.title-field {padding-bottom: 10px;max-width: 540px!important;}
.tabs.clearfix{width: 540px!important;}
.tag-label {color: rgba(0,0,0,.6)!important;}
  
.reblog-list {width:540px!important;padding: 0px;}
.reblog-list-item {padding: 5px 0px;}
.control-reblog-trail {margin: 0px;width:540px!important;}
.control-reblog-trail .reblog-trail-post-content {width:540px!important;padding: 0px 0px 0px 0px;}
  
.post-form--form {margin:0px!important;padding:0px!important;}
  
.media-wrapper ~ .caption-wrapper .control-reblog-trail {margin-top: 0px!important;width:540px;}
.media-wrapper {padding-bottom:5px;}
.media-dropzone-container.media-container {margin:0px;padding-bottom:5px;}
.media-container.photo-media-container, .audio-media-container.media-container.direct{margin:0px;}
.media-container {margin: 0px;}
.media-container.photo-media-container--can-add:not(.photo-media-container--show-photobooth){margin:0px;}
.media-container .photoset, .media-container .webcam, .media-container .media-dropzone, .media-container .dropzone-overlay, .media-container .video-container {margin:0px;}

.post-composer_link-post.loaded .link-editor{width:540px;margin:0px;}
.post-composer_link-post .caption-field{padding:0px;margin:0px;}
.tmblr-full {margin-left:0px!important;margin-right: 0px!important;}

/*min heights*/
.editor-placeholder{width:100%;}
.editor.editor-richtext, .editor.editor-plaintext.has-text{min-height:5px!important;width:100%;padding:0px!important;}
.post-typography-update .post .post-content .title-field {margin-top:0px;width:100%;}

/*overlay buttons*/
.media-container .remove-button, .media-container .remove-button.active {margin-right: 20px;margin-top: 15px;}
.media-container .media-overlay .overlay-button, .media-container .media-overlay.active .overlay-button {margin-left: 0px;}

/*two pictures side by side in the text field*/
.nfp_row{max-height: 250px;}
.post.post_full div.npf_row{margin-left: 0px;margin-right: 0px;}
}
