style.css:
@-moz-document domain("app.groupme.com") {
.chat-messages, .message, .chat-header, .chat-sidebar, .chat-sidebar *, .chat-input, .mousetrap, .dropdown-menu, .dropdown-menu a,
.form-horizontal, .form-actions, .device-notifications-toggle, .paste {
    background-color: #222 !important;
    color: #bbb !important;
}
.inline-downloader, input {
    background-color: #333 !important;
    color: #bbb !important;
}
.timestamp, .system .body {
    background-color: #222 !important;
    color: #f2f2f2 !important;
}
body {
    background-color: #000 !important;
    color: #F2F2F2 !important;
}
.navbar-inner, .dropdown-menu a:hover, button {
    background-color: #025 !important;
    color: #bbb !important;
}
.burger-box:hover, button:hover {
    background-color: #047 !important;
    color: #F2F2F2 !important;
}
.paste {
    border-left: rgba(255, 255, 255, 0.2) solid !important;
}
}
