@-moz-document regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?."), regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"), regexp("https?://encrypted.google.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"), regexp("https?://encrypted.google.(com|([a-z]{2}))(.[a-z]{2})?.") {
/* @import url('https://fonts.googleapis.com/css?family=Monoton|Nosifer'); */

:root {
--bg1: #fff !important;          /*  var(--bg)   */
--bg2: #eee !important;          /*  var(--bg2)   */  
--bg3: #f0f0f0 !important;             /*  var(--bg3)   */
--text1: #444 !important;             /*  grey  */  
--text2: #222 !important;             /*  green   */  
--text3: #888 !important;             /* blue  */
    
--accent: #a57983 !important;    
}     


				/* Basics */

a, a:hover, span, a:-webkit-any-link {
    text-decoration: none !important;
}

a, a:visited, a:focus, a:hover, a:link, a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, ._Iqg, .irc_pt {
	color: var(--accent);
}

a:visited, #res h3.r:hover {
    opacity: 0.5;
    color: var(--text3);
}

.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #_bGc>i, .bc a:link {
    color: var(--text3);
}

* {
    transition: ease-in-out .3s;
}

		/* Logo */

#hplogo::after, #logocont::after {
    content: "Google";
    
    	/* Halloween Font */
/*     font-family: "Nosifer", cursive; */
    
    	/* Default Font */
 	font-family: "Monoton", cursive;
    
    text-shadow: 8px 8px 5px rgba(0,0,0,0.3);
    color: var(--accent);
}

	/* Logo on Main Page */

#hplogo::after {    
    font-size: 55px;
    margin-top: 150px;
}

	/* Logo on Search Page */

#logocont::after {
    font-size: 25px;
}

	/* Greyscale Images */

.gsri_a, ._wtf svg {
    filter: grayscale(100%);
}

	/* Remove/Invert Apps Icons */

#gbwa > div.gb_Fc > a {
    display: none;
    filter: invert(100%);
    opacity: 1;
    margin-top: 0px;
}


	/* Remove Horizontal Scrollbar */

body {
    overflow-x: hidden;
}
					/* Google Search */
  
     /* header */
  
div.sfbgg {
  background-color: var(--bg2) !important;
  background: url("http://puu.sh/xS1Tm/d2df8d3eff.jpg") !important;
  background-size: cover !important;   
  }    
.hn:before, .b4, div.sfbg, div.sfbgg, .jhp > #gb, #mngb, .w-asV.aiw, .gb_ue {
