}

	/* tabs */

#hdtb-msb .hdtb-tl-sel:hover, #hdtb-msb #hdtb-more, .hdtb-tl .hdtb-tl-sel, .hdtb-tl, #hdtb-msb .hdtb-mitem.hdtb-imb, #hdtb .hdtb-mitem a, #hdtb-msb #hdtb-more, #hdtb-msb #hdtb-tls {
    border-top: none;
    border-left: none;
    border-right: none;
    padding: 10px;
    background: transparent;
    box-shadow: none;
    margin-top: 0px;
}

	/* selected */

#hdtb-msb .hdtb-mitem.hdtb-msel, #hdtb-msb .hdtb-mitem.hdtb-msel-pre, #hdtb-msb .hdtb-tl-sel, #hdtb-msb .hdtb-tl-sel:hover, #hdtb-msb #hdtb-more:hover, #hdtb-msb #hdtb-tls:hover, #hdtb .hdtb-mitem a:hover {
    color: var(--accent);
    border-bottom: 3px solid var(--accent);
}

	/* remove link dropdown, report links and useless search related things */

a.ab_button, #irl_r_a, ._qNo, #rhs .kno-ftr.rhsvw, a.duf3 {
    display: none;
}

	/* remove footer */

#fbar._Zvd {
