}

				/* Google Images */	

#_YTc, #irc_cc, .irc_bg, #irc_bgl {
    background: var(--bg2) !important;
}

	/* remove seperators */
.irc_land .irc_sep, .irc_land .irc_infosep, #irc_pc {
    display: none;
}

	/* Suggested Searchs */

a.chip._mkn, a.chip._tkn, a.chip._okn, a.chip._nkn, a.chip._rkn, a.chip._qkn, a.chip._skn, a.chip._pkn {
    background: var(--accent);
}

	/* buttons */

.irc_but {
    background: var(--bg3) !important;
    border: none;
    box-shadow: none;
    color: var(--text1);
    text-shadow: none;
}
.irc_but:hover {
    background: var(--bg1) !important;
    border: none;
    box-shadow: none;
    color: var(--text2);
    text-shadow: none;
}

	/* remove useless copyright sections on image search */

#irc_cc > div:nth-child(1) > div.irc_b.i8152.irc_mmc > div.irc_ris > div > div._B3l, .irc_land .irc_ft {
    opacity: 0;
}

	/* related images */

.irc_rimask.irc_rist img, .irc_rimask.irc_rist {
    opacity: 1;
    outline: none;
}

.irc_rii {
    opacity: .9;
}
.irc_rii:hover {
    opacity: 1;
}

	/* image on hover info */

.rg_anbg, .rg_ilmbg {
    background: rgba(0,0,0,0.4);
    padding: 10px;
    font-size: 15px;
