}

.sbico-c {
	height: 45px;
    top: 0px;
}

	/* search buttons */

#tsf > div.tsf-p > div.jsb > center {
    opacity: 0;
}
#tsf > div.tsf-p > div.jsb > center:hover {
    opacity: 1;
}

	/* logo */

#hplogo > a > img, ._bGb, #logocont > a > img, #logo > img, .logo-subtext {
