style.css:
@namespace url(http://www.w3.org/1999/xhtml);


/*\
 *
 * @name        Kaskus Ads Removal
 * @description Hides advertisement elements in Kaskus
 * @author      s4nji
 * @namespace   https://github.com/s4nji/kaskus-userstyles
 * @revision    4
 *
\*/


@-moz-document domain("kaskus.co.id") {

  .ads-lb-wrap, .kaskus-ads, .banner-frame, #adunit, #ads, .bottom-frame,
  #thread_post_list > .row > .col-xs-12 > .text-center
  
  {
    
    display: none !important;
    visibility: hidden !important;
    
    /* Overkill, but whatever */
    
    background: none transparent !important;
    border: 0 !important;
    outline: 0 !important;
    box-shadow: none !important;
    
    opacity: 0 !important;
    pointer-events: none !important;
    
    position: absolute !important;
    top: -9999px !important;
    left: -9999px !important;
    
    width: 0 !important;
    max-width: 0 !important;
    height: 0 !important;
    max-height: 0 !important;
    
  }
  
}
