style.css:
/* i really want this to be global */



/* the little spinny button that controls the tray */
.opener, .opener.closed, .opener.open, div.opener { 
    display:none;
    width: 0;
    height: 0;
    overflow: hidden;
    -webkit-transform:none !important;
        -moz-transform:none !important;
        -ms-transform:none !important;
        -o-transform:none !important;
    -webkit-transition:none !important;
        -moz-transition:none !important;
        -ms-transition:none !important;
        -o-transition:none !important;
}

.editor-slot, .editor-slot .inline-controls {
    display:block !important;
    width:auto;
    height:auto;
}


.inline-controls { /* parent to both 'bubbles' and 'tray' */
    position: absolute;
        left: 0px;
        top: -28px !important;
    display: inline !important;
}
    .tray, .editor-slot .inline-controls .tray, .tray .velocity-animating {
        display: block;
            top: 0;
            left: 0px !important;
        z-index:99999;
        height: 24px;
        border-radius: 3px;
        position: absolute;
        opacity: 1.0 !important;
        -webkit-transform:none !important;
            -moz-transform:none !important;
            -o-transform:none !important;
        -webkit-transition:none !important;
            -moz-transition:none !important;
            -o-transition:none !important;
    }
    .control.add-inline-embed { /* embedding a video doesn't even work, so i just removed it */
        width:0;
        height:0;
        overflow:hidden;
        display:none !important;
    }
    .inline-controls { /* parent to 'tray' */
        position: absolute;
            right: 0px;
            top: -28px !important;
        display: inline !important;
    }
    .tray {  
  		padding-bottom:8px;
        max-width:150px !important;
    }
  
.post-form--tag-editor, .url { z-index: 999999; }
