style.css:
@-moz-document url(http://chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/manage.html) {}
/*
	All Stylish pages (Manage, Edit, popup menu)
*/
/*@-moz-document url-prefix("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/") {*/
@-moz-document regexp("chrome-extension://[^/]+/.*") {
body, body#stylish-edit {
  background: /*--url('http://static.tumblr.com/nyr6guj/JbTmvi9bh/1__1_.gif')--*/ url('data:image/gif;base64,') fixed white;
  color: #CF358E;
}
a, a:visited {color: #CF358E;}

}

/*@-moz-document url("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/popup.html") {*/
@-moz-document regexp("chrome-extension://[^/]+/popup.html") {
/*
	Popup menu (toolbar icon)
	chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/popup.html
*/
body {
  background: rgba(207,53,142,.1);
}
.disabled {
  color: inherit;
  opacity: .5;
}
}

/*@-moz-document url("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/manage.html") {*/
@-moz-document regexp("chrome-extension://[^/]+/manage.html") {
/*
	Style manager
	chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/manage.html
*/
button {
  color: #CF358E;
}
button:hover {
  color: #ff6ebe;
  border-color: #CF358E;
}
}

/*@-moz-document url-prefix("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/edit.html") {*/
@-moz-document regexp("chrome-extension://[^/]+/edit.html.*") {
/*
	Edit style
	chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/edit.html
*/
input {color: inherit}
}


/*@-moz-document url-prefix("chrome-extension://dhdgffkkebhmkfjojejmpbldmpobfkfo/options.html") {*/
@-moz-document regexp("chrome-extension://dhdgffkkebhmkfjojejmpbldmpobfkfo/options.html.*") {
body.main, div.tv_contents {
  background: /*--url('http://static.tumblr.com/nyr6guj/JbTmvi9bh/1__1_.gif')--*/ url('data:image/gif;base64,') fixed white;
  color: #CF358E;
}
*, a, a:visited {color: #CF358E;}
}
