style.css:
@-moz-document domain("docs.unity3d.com") {
html{
background-color: #1A1B1C ;
}

div.content {
    background-color : #1A1B1C ;
}

h1, h2, p, span, a {
 color : #fff !important;
}

a:link{
color: #fff !important;
}

td {
border-style: ridge;
border-width: 2px ;
border-color: #fff;
 color: #fff;
 background-color: #282B2D ;
}

pre {
color: #fff;
background-color: #151515;
border: none;
}

code {
color: #fff;
background-color: #252525;
}

pre.codeExampleRaw, pre.codeExampleCS, pre.codeExampleJS{
color: #fff;
background-color: #000000;
}

td.desc {
border-width: 2px ;
border-color: #fff;
 color: #CACCD0;
 background-color: #282B2D ;
}

table.list tr:hover{ 
outline: #009393 2px solid ; 
}

.signature-CS, .copy, .version-number, .signature-JS{
 color: #E0E0E0;   
}
}
