style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
  .html5-video-player.playing-mode > .html5-video-controls {
    /*opacity: 0;
    transition: opacity .5s ease-out .1s;*/
    margin-bottom: -28px;
    transition: margin .5s ease-out .1s;
  }
  .html5-video-player.playing-mode:hover > .html5-video-controls {
    /*opacity: 1;*/
    margin-bottom: 0;
  }
  .html5-video-player.playing-mode .annotation.iv-branding {
    bottom: -8px;
    transition: bottom .5s ease-out .1s;
  }
  .html5-video-player.playing-mode:hover .annotation.iv-branding {
    bottom: 20px;
  }
  .html5-video-player.playing-mode .annotation.iv-promo {
    bottom: -8px;
    transition: bottom .5s ease-out .1s;
  }
  .html5-video-player.playing-mode:hover .annotation.iv-promo {
    bottom: 20px;
  }
}
