style.css:
@-moz-document domain(trello.com) {

/******************************************************
 *                                                    *
 * UI                                                 *
 *                                                    *
 ******************************************************/

/* Makes UI font similar to system */
html, body, p, li, td, th, input, textarea, select, button {
  font-family: -apple-system, system-ui, "Segoe UI", Roboto, "Noto Sans", Ubuntu, "Droid Sans", "Helvetica Neue", sans-serif;
}

/******************************************************
 *                                                    *
 * LISTS                                              *
 *                                                    *
 ******************************************************/

.list {
  background: rgba(255,255,255,0.2);
}

/******************************************************
 *                                                    *
 * LABELS & BADGES                                    *
 *                                                    *
 ******************************************************/

/* Makes label text visible on cards */
.list-card-labels .card-label {
  display: inline-block;
  height: auto !important;
  width: auto;
  min-width: 20px;
  padding: 0 4px !important;
  font-size: 8px !important;
  font-weight: bold;
  line-height: 16px;
  text-shadow: none;
  text-transform: uppercase;
  letter-spacing: 0em;
}

/* Fixes lineheight issues for badges */

.badge {
  border-radius: 3px;
  font-size: 11px;
  line-height: 18px;
}


}
