style.css:
@-moz-document url-prefix("https://www.google.com.tw/") {
/*HomePage-Background*/
body[class ~= "hp"]{
    background-attachment: fixed !important;
    background-image:url(http://i.ytimg.com/vi/N6W_p_0oTBE/maxresdefault.jpg)!important;
    background-size:cover!important;
    background-repeat:no-repeat!important;
}

/*HideGoogleLogo*/
div[id="hplogo"]{
    background-image:none!important
}
div[id="hplogo"] div{
    display:none!important
}

/*Serch-Backcolor*/
div[class="sbibod "]{
    background-color:rgba(159, 244, 251, 0.5)!important
}
div[class="gstl_0 sbib_a"]{
    background-color:rgba(0, 0, 0, 0)!important
}

/*Serch-Posation*/
body[class ~= "hp"] div[class="jhp big"]{
    top:340px!important
}

/*Button-Background*/
input[type="submit"]{
    background-image:none!important;
    background-color:rgba(159, 244, 251, 0.5)!important
}
}
