style.css:
@-moz-document domain("clien.net") {
  #aside {
    display: none !important;
  }

@font-face {
    font-family:'RIDIBatang';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_twelve@1.0/RIDIBatang.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

  h4, #writeContents, #writeContents > p, #writeContents > p > span, #writeContents > p > span > span, 
  #writeContents > div, #writeContents > div > span, #writeContents > div > span > span,
  .user_id, .member, .reply_right, .reply_content {
    font-family: "RIDIBatang" !important;
    font-size: 1.15rem !important ;
    line-height: 1.85em !important;
  }

  h4, #writeContents, .member, .reply_right {
    font-size: 1.15rem !important ;
    line-height: 1.85em !important;
  }

  #writeContents > p, .reply_content {
    font-size: 1em !important ;
    line-height: 1.85em !important;
  }
}
