style.css:
@-moz-document url("https://trello.com/"), regexp("^https?://trello.com/[cb]{1}.*$") {
/**
* Trello Solarized Dark Grey v0.6 (2018-08-28)
*
* https://userstyles.org/styles/125265/trello-solarized-dark-grey
* https://github.com/ristomatti/trello-solarized-dark
*
* The MIT License (MIT)
* https://opensource.org/licenses/MIT
*
* (c) 2018 Ristomatti Airo <ristomatti@kallio.io>
*/

/* Main area */
body {
	color: #eee8d5;
	background-color: #141414 !important;
}


/* Header section */
#header {
  background: transparent;
}


/* Boards list page */
.body-tabbed-page {
  background: #1b1b1b !important;
}

/* Boards list page board tile */
.board-tile {
	color: #eee8d5 !important;
}

/* Boards list page button */
.boards-page-board-section-header-options-item {
  background-color: #2e2e2e !important;
  color: #eee8d5 !important;
}

/* Add board button */
.board-tile.mod-add {
  background-color: #212121 !important;
}


/* Board page */

/* Board title */
.board-header-btn {
	color: #eee8d5;
}

/* Lists */
.list {
	color: #eee8d5;
	background-color: #1b1b1b;
}

/* Cards */
.list-card {
	background-color: #272727;
	border-bottom: none;
}

/* List card header */
.list-header-name {
 	color: #93a1a1 !important;
}

/* List card input */
.list-card-composer-textarea {
	color: #93a1a1 !important;
}

/* List card hover */
.list-card:hover,
.open-card-composer:hover {
  background-color: #313131;
  color: #eee8d5;
}

/* Card titles */
.list-card-title {
	color: #93a1a1 !important;
}

/* Right side menu */
.board-menu {
	color: #93a1a1 !important;
	background-color: #272727;
}
.board-menu a.board-menu-section-header {
	color: #eee8d5;
}
.board-menu a {
	text-decoration: none;
	color: #268bd2;
}
hr {
	background: #93a1a1 !important;
}


/* Card details */
.card-detail-window {
	background-color: #212121;
}

.card-detail-window .action-comment {
    background-color: #212121;
    color: #93a1a1;
}

.card-detail-window a {
    color: #268bd2;
    text-decoration: none;
}

.card-detail-window a:hover {
    color: #eee8d5;
}

.card-detail-window a:visited {
    color: #6c71c4;
}

/* Solarized colors */

/* Green */
.card-label-green,
*[style*="background-color: rgb(81, 152, 57)"],
*[style*="background-color: #519839"] {
	background-color: #859900 !important;
}

/* Yellow */
.card-label-yellow,
*[style*="background-color: rgb(210, 144, 52)"] {
	background-color: #b58900 !important;
}

/* Purple */
.card-label-purple,
*[style*="background-color: rgb(137, 96, 158)"],
*[style*="background-color: #89609E"] {
	background-color: #6c71c4 !important;
}

/* Blue */
.card-label-blue,
*[style*="background-color: rgb(0, 121, 191)"] {
   background-color: #268bd2 !important;
}

/* Red */
.card-label-red,
*[style*="background-color: rgb(75, 191, 107)"],
*[style*="background-color: #4BBF6B"] {

	background-color: #dc322f !important;
}

/* Pink */
.card-label-pink,
*[style*="background-color: rgb(205, 90, 145)"] {
   background-color: #d33682 !important;
}

/* Black */
.card-label-black {
	background-color: #141414 !important;
}

/* Orange */
.card-label-orange,
*[style*="background-color: rgb(176, 70, 50)"] {
	background-color: #cb4b16 !important;
}

/* Cyan */
*[style*="background-color: rgb(0, 174, 204)"],
.card-label-cyan {
	background-color: #2aa198 !important;
}

/* Base00 (lime) */
*[style*="background-color: #51e898"],
.card-label-lime {
   background-color: #657b83 !important;
}

/* Grey tile background */
*[style*="background-color: rgb(131, 140, 145)"],
*[style*="background-color: #838C91"] {
	background-color: #141414 !important;
}


/* Scrollbar */
::-webkit-scrollbar {
	max-width: 10px !important;
	max-height: 10px !important;
	background: #181818 !important;
}
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner {
	background: #181818 !important;
}
::-webkit-scrollbar-thumb {
	border-radius: 5px !important;
	background: rgba(80, 80, 80, .6) !important;
}
::-webkit-scrollbar-thumb:hover {
	background: rgba(96, 96, 96, .6) !important;
}
}
