style.css:
@-moz-document domain("facebook.com") {
             [data-pagelet="Stories"]
             ,[role="complementary"]
             ,[data-pagelet="root"] > div > [data-visualcompletion="ignore"]
             ,[role="navigation"]
             ,[role="banner"]
             ,[role="main"] > div > div > div:not(:last-child)
             ,[role="main"] > div > div > div:nth-child(4) > div > div:nth-child(3)
             ,[role="main"] > div > div > div:nth-child(4) > div > div:nth-child(4)
             {display:none!important;}
             [data-pagelet="root"] > div > div > div > div {min-height:100%!important;top:0px!important;}
}
