style.css:
@-moz-document domain("workflowy.com") {


/* Based on the "Workflowy Re-Painter" style by @arose: 
   https://userstyles.org/styles/123554/workflowy-re-painter      
   
   Customize the "Painter for WorkFlowy" Chrome extension colors:
   https://chrome.google.com/webstore/detail/painter-for-workflowycom/boeplfofdabogkgimajcpadbnfcmkmek */


/* WorkFlowy Painter colors */

.wfcolor-red	     {	background-color: #FFB5B5 !important; }
.wfcolor-orange	     {	background-color: #FFD8B5 !important; }
.wfcolor-yellow      {	background-color: #FFFAB5 !important; }
.wfcolor-lime        {	background-color: #E1FFB5 !important; }
.wfcolor-olive	     {	background-color: #B5FFC9 !important; }
.wfcolor-green	     {	background-color: #CCFFB5 !important; }
.wfcolor-teal        {	background-color: #B5FFD7 !important; }
.wfcolor-aqua	     {	background-color: #B5FFFC !important; }
.wfcolor-blue	     {	background-color: #B5E8FF !important; }
.wfcolor-navy	     {	background-color: #B9B5FF !important; }
.wfcolor-fuchsia     {	background-color: #F1B5FF !important; }
.wfcolor-purple	     {	background-color: #D3B5FF !important; }
.wfcolor-maroon      {	background-color: #C08F8F !important; }
.wfcolor-silver      {	background-color: silver  !important; }
.wfcolor-gray        {	background-color: gray    !important; }
.wfcolor-black       {	background-color: black   !important; }
.wfcolor-white       {	background-color: white   !important; }

    
    
/* Invisible WorkFlowy Painter tags */

[title~="#red"    ]   { color: #FFB5B5 !important; }
[title~="#orange" ]   { color: #FFD8B5 !important; }
[title~="#yellow" ]   { color: #FFFAB5 !important; }
[title~="#lime"   ]   { color: #E1FFB5 !important; }
[title~="#olive"  ]   { color: #B5FFC9 !important; }
[title~="#green"  ]   { color: #CCFFB5 !important; }
[title~="#teal"   ]   { color: #B5FFD7 !important; }
[title~="#aqua"   ]   { color: #B5FFFC !important; }
[title~="#blue"   ]   { color: #B5E8FF !important; }
[title~="#navy"   ]   { color: #B9B5FF !important; }
[title~="#fuchsia"]   { color: #F1B5FF !important; }
[title~="#purple" ]   { color: #D3B5FF !important; }
[title~="#maroon" ]   { color: #C08F8F !important; }
[title~="#silver" ]   { color: silver  !important; }
[title~="#gray"   ]   { color: gray    !important; }
[title~="#black"  ]   { color: black   !important; }
[title~="#white"  ]   { color: white   !important; }

    

/* Padding */

.wfcolor-red, 
.wfcolor-orange, 
.wfcolor-yellow, 
.wfcolor-lime, 
.wfcolor-olive, 
.wfcolor-green, 
.wfcolor-teal, 
.wfcolor-aqua, 
.wfcolor-blue, 
.wfcolor-navy, 
.wfcolor-fuchsia, 
.wfcolor-purple,  
.wfcolor-silver,  
.wfcolor-white {
    color: black !important; padding: 3px 10px 3px 10px; 
    box-shadow: 3px 3px 3px #E6E6E6;
    margin-top: 10px;
    margin-bottom: 10px;

}

.wfcolor-black, 
.wfcolor-gray,
.wfcolor-maroon {
    color: white !important; padding: 3px 10px 3px 10px;
    box-shadow: 3px 3px 3px #E6E6E6;
    margin-top: 10px;
    margin-bottom: 10px;
}
    
}
