style.css:
@-moz-document url-prefix("https://demo.rocket.chat/") {
code {
    font-family: Monaco,Menlo,Consolas,"Courier New",monospace !important;
}

.account-box .info {
    background-color: #333 !important;
}

.side-nav .rooms-list {
    background-color: #444 !important;
}

.side-nav .unread-rooms {
    background-color: #6D6D6D !important;
}

.side-nav .unread {
    background-color: #6D6D6D !important;
}

.side-nav .footer {
    background-color: #333 !important;
}

.fixed-title {
    background-color: #444 !important;
    color: white !important;
}

.messages-box {
    background-color: #333;
}

.messages-box ul  {
    color: #CDCDCD;
}

.messages-box ul > li:nth-child(odd) {
    background-color: #404040;
}
.messages-box ul > li:nth-child(even) {
    background-color: #434343;
}

.message .user {
    color: #D0D0D0;
}

div.body a {
    color: #D0D0D0 !important;
}

.mention-link {
    color: #EFEFEF !important;
}

.message.new-day:before {
    background-color: #333 !important;
    color: #EEEEEE;
    font-size: 1.1em;
}

.message .user:hover {
    color: #EEEEEE;
}


.messages-container .footer {
    background-color: #434343 !important;
}

.messages-container .message-form>div>.file {
    background-color: #434343 !important;
    border-color: #999 !important;
}

.messages-container .message-form textarea {
    background-color: #434343 !important;
    border-color: #999 !important;
    color: #EEEEEE;
}

.icon-paper-plane, .send-button {
    background-color: #434343 !important;
    
}

.flex-tab-bar, .tab-button {
    background-color: #444 !important;
     border-color: #555 !important;
    color: #EEEEEE;
}

.flex-tab-bar {
    border-color: #757575 !important;
}
}
