style.css:
@-moz-document domain("habitica.com") {
.site-header, .header-wrap, .sticky-wrapper {
    height: 30px !important;  
    background: white !important;
    }

.shop_gold, .shop_silver, .Pet_Currency_Gem2x, .npc_bailey, .shop_spell {
    background-image: none !important;
    }

.rewards .reward-img, .herobox, .habitica-emoji, .markdown-img, .hero-stats {
    display: none !important;
	}

  nav.toolbar li.toolbar-button a, /* toolbar and dropdown text */
  nav.toolbar li.toolbar-button-dropdown,
  nav.toolbar li.toolbar-button-dropdown a,
  nav.toolbar ul.toolbar-options a,
  nav.toolbar .toolbar-button-dropdown.highlight,
  nav.toolbar .toolbar-button-dropdown.highlight a,
  .toolbar-nav .toolbar-button-dropdown.highlight:hover,
  .toolbar-currency.gold,
  .toolbar-currency.silver,
  .gem-wallet,
  .toolbar .glyphicon {
      color: black!important;
      text-shadow: none;
  }

.toolbar-notifs div, .toolbar-audio div, .toolbar-settings div { /*dropdown background */
    background-color: white !important;
}

  .toolbar-nav .toolbar-button > a:hover,   /* color of upper left buttons on mouse hover */
  .toolbar-nav .toolbar-button button:hover,
  .toolbar-nav .toolbar-button-dropdown > a:hover,
  .toolbar-nav .toolbar-button-dropdown button:hover,
  .toolbar-nav .toolbar-button-dropdown.highlight > a:hover,
  .toolbar-nav .toolbar-button-dropdown.highlight button:hover {
      background-color: yellow !important;
  }

[class^="quest_"] { /* removes quest boss image */
  display: none;
}

[class^="quest_"] + .hero-stats { /* removes quest boss HP bar */
    display: none;
}
}
