style.css:
@-moz-document domain("myanimelist.net") {
  ._unit {
    opacity: 0;
    height: 0 !important;
  }
  
  .border_top {
    margin: 0px !important;
  }

  .pxInta, .pxInta:focus, .pxInta:link, .pxInta:visited, .pxInta:active, .pxInta:hover {
    border: 0px !important;
    background-color: transparent !important;
    border-bottom: none !important;
    color: #000 !important;
    font-weight: normal !important;
    text-decoration: none !important;
    padding-bottom: 0px !important;
    display: inline !important;
    font-size: inherit !important;
  }
  
  td[style="height:50%;background:#4f74c8;color:#f6f6f6;font-size:14px;"], td[style="height:20%;background:#ebebeb;color:#545454;font-size:11px;"], td[style="height:30%;background:#1E439B;color:#fff;font-size:14px;"] {
    display: none !important;
    height: 0px !important;
    width: 0px !important;
  }
}
