style.css:
@-moz-document domain("login.taobao.com") {
    /*     登录页背景图片 */
    div.login-newbg {
        background-image: url("http://wx3.sinaimg.cn/large/0065PkNmgy1ffm1ccv8scj31kw0zke82.jpg") !important;
    }

    /*   页面主体,缩小+移位+毛玻璃框+盒阴影 */
    div.content-layout {
        background: rgba(255, 255, 255, 0.4);
        box-shadow: 0 0 0 1px rgba(255, 255, 255, .3) inset, 0 .5em 1em rgba(0, 0, 0, 0.6);
        border-radius: 2em;
        width: 900px !important;
        height: 490px !important;
        position: absolute;
        top: 45px;
    }

    /*   扩宽登录表单容器并更改其位置 */
    .login-box-warp {
        width: 650px;
        top: 60px;
        right: 120px;
    }

    /* 主要作用于天猫登录页的登录表单，此处对淘宝登录影响不大*/
    .bd {
        width: 650px;
    }

    #J_LoginBox {
        border-radius: 1em;
        position: absolute;
        width: 650px;
        box-shadow: 0 0 0 1px rgba(255, 255, 255, .3) inset, 0 .5em 1em rgba(0, 0, 0, 0.6);
        padding-bottom: 7px;
    }

    /*   覆盖在背景上的广域广告链接，登录提示，二维码与账号密码的切换按钮，二维码下方的'密码登录'和'注册'链接 */
    .login-adlink,
    .login-tip,
    #J_Quick2Static,
    div.login-links:nth-child(5) {
        display: none !important;
    }

    /*   显示账号密码登陆 */
    #J_StaticForm {
        display: inline-block !important;
        padding-right: 35px;
        border-right-style: dashed;
    }

    /*   将二维码登录移动至容器右上角 */
    .quick-form {
        width: 300px;
        float: right !important;
    }
}
