style.css:
@-moz-document domain("wenku.baidu.com"), domain("youke.baidu.com") {
/* 屏蔽不需要的内容 */

.ec-ad,
.page-banner,
.ad-icon,
.banner-ad,
.top-ads-banner,
.ad-taishan-bar,
.toolsbar-vip-wrap,
.end-download-bottom-layout,
.search-aside-adWrap,
.tiger-shifen-dialog,
.tiger-lossUser-dialog,
.search-aside-newadWrap,
div[class^="ec_src"],
#wkgg,
#xs-task,
#user-my-class,
#share-tips,
#banurl,
#nav-gaokao,
#guess-like-doc,
#lastcell-dialog,
#add-new .frist-item,
#topSearchBox .s_tools,
#doc-aside-border .commerce-flow,
div[id^='wkad'],
div[id^='html-reader-AD'],
div[id^='html-reader-banner'] {
    display: none!important;
}
/* 特殊元素处理 */

#adbg,
#activity-tg .num {
    background: none!important;
}
/*  屏蔽7周年广告 */

.fix-searchbar-wrap,
.bg-opacity {
    top: 0px !important;
}
}

@-moz-document domain("youke.baidu.com") {
#packgift,
#pop_lastcell {
    display: none!important;
}
}

@-moz-document url-prefix("https://wenku.baidu.com/search"), url-prefix("http://wenku.baidu.com/search") {
#fengchaoad {
    display: none!important;
}
}
