style.css:
/*@-moz-document url-prefix(http) { }*/

@-moz-document url("chrome://devtools/content/inspector/inspector.xul") {
  window.theme-light {
    --src-color: var(--theme-highlight-blue);
    --src-color: purple;
  }
  window.theme-dark {
    --src-color: var(--theme-highlight-green);
    --src-color: orchid;
  }
  .link.theme-link {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;

    max-width: 80%;
    color: var(--src-color);
  }
}
