style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("roblox.com") {
  #nav-robux {
    background-position: 0px 12.5%;
  }
  
  #nav-robux-icon:hover #nav-robux {
    background-position: -28px 12.5%;
  }
  
  span.robux {
    background-image: url("https://static.rbxcdn.com/images/Tickets.png");
    color: #A61;
  }
  
  .SalesText, .text-robux, .text-robux-lg {
    color: #A61;
  }
  
  .icon-robux-16x16, .icon-robux-20x20, .icon-robux-gray-16x16 {
    background-position: 0px 71%;
  }
  
  .icon-robux {
    background-position: 0px 71% !important;
  }
  
  .btn-primary-md, #item-container .btn-primary-lg, .PurchaseButton:hover {
    background-color: #A90 !important;
    border-color: transparent !important;
  }
  
  .icon-robux-white {
    background-position-y: 71% !important;
  }
}
