style.css:
@-moz-document domain("numpy.org"), domain("scipy.org"), url-prefix("http://scipy.github.io") {
  body {
    color: #AAA;
    background-color: #000;
  }

  div.sphinxsidebarwrapper, div.container, div.main, p, div.admonition, p.first, p.last {
    color: #AAA;
    background-color: #111;    
  }
  
  tt, pre, span.pre, tt.literal {
    color: #CCC;
    background-color: #333;    
  }
  
  div.bodywrapper {
    color: #AAA;
    background-color: #111;
  }
  
  div.bodywrapper dt, div.bodywrapper dd {
    color: #AAA;
    background-color: #111;
  }
  
  div.bodywrapper pre {
    color: #AAA;
    background-color: #111;
  }

  div.bodywrapper li {
    color: #AAA;
    background-color: #111;
  }
}
