style.css:
@-moz-document domain("inoreader.com") {

#reader_pane.reader_pane_view_style_4, #reader_pane.reader_pane_view_style_4 .unsubscribed_feed_suggestions_label {
    background-color: black;
}

/* Overall Background color */
#tree_pane, #subscriptions_fieldset, #subscriptions_buttons, #sb_rp_tools, #sb_tp_search_overlay {background: black;}

/* All articles (i.e. read articles) */
.ar.article_subscribed {background: black;}

/* Unread articles */
.ar.article_unreaded {background: #033}

/* pop up article thing */
#article_dialog, .article_full_contents, .article_footer {background: #111;}

.unread_cnt {color: pink; font-size: 16px;}

div.article_current, div.article_current_collapsed, div.article_magazine.article_current {background: #400;}

A.bluelink:link {color: #f186aa;}

}
