style.css:
@-moz-document domain("facebook.com") {
	*[dir=rtl],
	html[dir=rtl] > body,
	div[dir=rtl],
	span[dir=rtl],
	.webMessengerMessageGroup ._38 p,
	._6nm,
  	.direction_rtl,
	._1mk,
	html[dir=rtl] *	{
		font-family: "Mehr Nastaliq Web",  Tahoma !important;
		font-size: 150% !important;
		line-height: 180% !important;
	}
}

@-moz-document domain("twitter.com") {
    *[dir=rtl],
    div[dir=rtl],
    span[dir=rtl],
    .tweet-text-rtl {
		font-family: "Mehr Nastaliq Web",  Tahoma !important;
		font-size: 140%;
		line-height: 180%;
    }
}

@-moz-document domain("ur.wikipedia.org") {
    *[dir=rtl],
    html[dir=rtl] > body,
    div[dir=rtl],
    span[dir=rtl],
    html[dir=rtl] * {
		font-family: "Mehr Nastaliq Web",  Tahoma !important;
		font-size: 105%;
    }
}
