style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("intra.epitech.eu") {

    /*
    ** Light   blue : #3498db
    ** Dark    blue : #2980b9
    ** Darker  blue : #34495e
    ** Darkest blue : #2c3e50
    */

    body {
        background-color: #132029;
        color: rgba(255, 255, 255, .7);
    }
    
    
    a {
        color: rgba(255, 255, 255, .8);
    }
    
    #auth-login fieldset legend,
    #content .bloc.top .data .item.title h1 {
       color: #709cc9;
    }
    
/* Dark shades
   ========================================================================== */
    
    #homeboard section,
    #wall #content .bloc .notices div.item,
    #planning #content .bloc.main .planner,
    #planning #content .bloc.main .planner .top,
    #planning #content .bloc.main .planner .top thead th,
    #planning #content .bloc.main .planner .top thead td,
    #planning #content .bloc.main .planner .scroll .v-header,
    #planning #content .bloc.main .planner .days .scroll .events,
    #profil .bloc.main .data,
    #content .bloc.top .rzone,
    #content .bloc.tabulation,
    #content .datagrid table tbody tr,
    #content .datagrid table tbody tr:nth-child(2n) {
        background-color: #293238;
    }
    
    #content .datagrid table thead th, #content .datagrid table tfoot th {
        background-color: #53585C;
        color: rgba(255, 255, 255, .6);
    }
    
/* Light shades
   ========================================================================== */
    
    #homeboard section .articles article p.warning {
        color: white;
    }
    
    #homeboard section .articles article .timeline span.date,
    #planning #content .bloc.main .planner .scroll .v-header,
    div.datagrid {
        color: rgba(255, 255, 255, .6);
    }
    
    #homeboard section.hidden p.is_hidden, #homeboard section p.is_empty,
    #profil .bloc.main .data > .infos .item.login span {
        color: rgba(255, 255, 255, .7);
    }
    
    #planning #content .bloc.main .planner .top thead th,
    #planning #content .bloc.main .planner .top thead td {
        color: rgba(255, 255, 255, .9);
    }
    
    #content .bloc.top .data .item.title
    
    #wall #content .bloc .notices div.item .data .links .date {
        color: #c3cacf;
    }
    
    #profil #flags.tab .item {
        background-color: #46555F;
    }
    
    #content .bloc.top .data .item.title h1 {
       color: #709cc9;
    }
    
    #content .datagrid table thead tr th,
    #content .datagrid table tbody tr th,
    #content .datagrid table tbody tr td {
        border-color: #53585C;
        border-bottom-color: #33383E;
    }
    
    #content .datagrid table thead th div div,
    #content .datagrid table tfoot tr {
        border-left-color: #293238;
    }
    
}
