style.css:
@-moz-document domain("duelingnexus.com") {

html {
    background: url(http://i.imgur.com/0ha8s84.jpg)!important;
    background-repeat: no-repeat;
}
div {
    /* */
}
body {
    font-size: 12px;
    line-height: 1;
    color: #FFF;
    background-color: #FFF;
    background-repeat: no-repeat;
}
hr {
    margin-top: 0px;
    margin-bottom: 0px;
    border: 0;
    border-top: 0px solid #eee;
}
.h2,
h2 {
    font-size: 0px;
}
.container-fluid {
    padding-right: 5%;
    padding-left: 33%;
    margin-right: auto;
    margin-left: auto;
}
.button {
    -webkit-transition-duration: 0.4s;
    transition-duration: 0.4s;
    opacity: 0.9;
    width: 49.8697%;
    color: #FFF;
    background-color: #000;
    border-color: #FFFFFF;
    display: ruby;
    margin-bottom: 0;
    font-size: 12px;
    font-weight: bold;
    line-height: 1.42857143;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    border: 0px solid #000;
}
}
