style.css:
@-moz-document domain("aliexpress.com") {
.pnl-shipping .shipping-line
{
    padding: 0 !important
}

[class="buynow-custom"] > [class="table-container"] > table *
{
    border-spacing: 0!important;
    min-width: 100% !important;
    min-height: 100% !important;
    font-size: 12px !important;
    text-align: center !important;
    color: #333 !important;
    border: 1px solid #000 !important;
    font-family: monospace !important
}

#feedback > iframe
{
    max-height: fit-content(100%) !important;
    height: fit-content(100%) !important;
}

#transction-feedback > div.feedback-list-wrap > *,
.feedback-item,
.big-photo-view,
.photo-view-main,
.photo-view-main *,
.photo-view-content,
.photo-view-content *
{
    min-width: auto !important;
    min-height: auto !important;
    max-width: 100% !important;
    max-height: 100% !important;
    height: auto !important;
    width: auto !important
}
}
