style.css:
@-moz-document regexp("((http)|(https))(://news.mydrivers.com/).+") {
body{
	background:#f3f2ee;
}
.news_box{
	border:none;
	background-color:#f3f2ee;
	-webkit-box-shadow:none;
	background-image: url(http://icons.mydrivers.com/2018/common/kkj_logo.png);
	background-repeat:no-repeat;
	background-position:150px 0px;
}
.news_right,
#f_menu,
.news_bt1_right,
.news_bt1_left,
.pathway_warp,
#wrapper,
.add_pc,
.baidu,
.zcdf,
.share,
.module_info,
.news_xg,
#commentsiframe,
.footer_about,
#footer,
.weixin,
#dangbei_down,
.search,
.top,
#Hits,
#span_commentscount,
a[title="点击可以复制本篇文章的标题和链接"],
a[title="点击收藏文章"],
#a_showhotnews_list_dia,
.news_xg2,
#taboola-below-article-thumbnails{
	display:none!important;
}
.news_left{
	border:none;
	margin: 10px 170px;
}
.news_n{
	background:#f3f2ee;
	margin-top:100px;
}
.news_bt{
	font-size:28px;
	text-shadow: 4px 4px 8px #bdbdbd;
	margin:0px 0 20px 0;
}
.news_info{
	font-size:18px;
	line-height:35px;
	text-indent:2em;
}
.pathway{
	width: auto;
	opacity:0;
	transition: all 500ms;
}
.pathway:hover{
	opacity:1;
}
.news_bt1_left{
	float: none;
	text-align:center;
}
.bqian{
	text-align:center;
	margin-bottom:20px;
}
.bqian a{
	opacity:0.3;
	transition: all 500ms;
}
.bqian a:hover{
	opacity:0.8;
}
@keyframes myttt{
	0%{
        opacity:0;
    }
    66%{
        opacity:0;
    }
    100%{
        opacity:1;
    }
}
.righttitle,
#hotday7,
#hotday30,
.taobao_add{
	display:none;
}
.pc_box{
	margin-top:-100px;
	position: relative;
	z-index:1000;
	background-color:#f3f2ee;
	border:none;
	padding: 180px 30px 50px 30px;
	background-image: url(http://icons.mydrivers.com/2018/common/kkj_logo.png);
	background-repeat:no-repeat;
	background-position:30px 50px;
}
.pc_bt1{
	height:60px;
}
.pc_info p{
	padding:0px 69px;
	font-size: 18px;
    line-height: 35px;
}
}
