style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("scikit-learn.org") {
   
  body
  {
    background: #314454;
  }
  
  
  div.content, div.content-wrapper, div.header-wrapper, div.topic, table *, table>*, colgroup
  {
    background-color: #182938;
    color: white;
  }
  
  
  label[for="nav-trigger"]
  {
    color: white;
    background: #314454;

  }

  div.note
  {
    
    background: #314454;
  }
  
  pre, h1, code
  {
    filter: invert(100%);
    
  }
  
  h1
  {
    color: black;
  }
  
 
}
