style.css:
@-moz-document domain("joyreactor.cc"), domain("reactor.cc") {
#header {width: 550px; margin-left: 700px;}

/* --- MainPage --- */
#content, #contentinner, .pagination_toggler  {background: none !important}
#page{box-shadow: 0px 0px 1px 1px rgba(0, 0, 0, 0.1)}
#pageinner {background: rgba(1111, 1111, 1111, 0.5) !important;}
#searchBar {opacity:0.85}
#contentInnerHeader {position:relative; left:-15px;margin: 0 1% 0 1%}
#tagArticle {width: 100%}
.post_top {width:105%}
/* --- MainPage --- */

/* --- Post --- */
.taglist {position: absolute;top:0px;width:650px}
.taglist a{text-decoration:none;}
.uhead_nick {float:right}
.uhead_nick img {border-radius:50px}
.uhead_nick a {float:left;margin-right:10px;text-decoration:none;font-size: 16px!important;position:relative;top:15px;}
.post_content table, .post_content div.image{display:flex;justify-content: center;}
.uhead_share {position:absolute;right:345px!important;bottom:-45px!important;opacity:1}
.favorite_link{border-radius:50px}
/* --- Post --- */


.comment {
    box-shadow: inset 0px 0px 1px 1px rgba(0, 0, 0, 0.1);
    background: #f0f0f0;
    border-radius: 0px;
}
.comment_list {border-left: solid 2px #fdb201 !important;}


a.next, a.prev,#navlist a:hover, input {border-radius: 55px;color:#000}
a.top_logo:before {content: "\21B5";position: fixed; left: 10px; font-size: 24pt; opacity: 0.7}

.pagination_main {margin: 25px 0px 25px 0px}

/* --- Islands --- */
.article, .sidebar_block, .blog_results, #tagArticle {margin:0px;margin-bottom:20px;background:#fff;padding:15px;z-index:1;box-shadow: 0px 0px 1px 1px rgba(0, 0, 0, 0.1);}
.blog_results {margin-bottom: 40px;}
/* --- Islands --- */

.sidebar_block {position:relative;top:-40px;}
.sidebar_block {padding:5px!important}
.sidebar_block h2, #tagList {text-align:center}


/**del**/
.share_vk, .share_fb, .share_twitter {display:none!important;}
/**del**/

* {transition: .3s!important}

/*
#content:nth-child(n+1):nth-child(-n+7){display:none}
*/
}
