style.css:
@-moz-document domain("www.youtube.com") {
/* NavTop Logo */
#logo #logo-icon svg path {
	fill: #bb0000;
}

/* Resume Progress Bar Playback */
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
	background: #bb0000;
}

/* Video Player Progress bar */
.ytp-progress-bar  .ytp-progress-list .ytp-play-progress.ytp-swatch-background-color{
	background: #bb0000;
}
.ytp-progress-bar .ytp-scrubber-button{
	background: #bb0000;
}

/* Live Buttons*/
.badge.badge-style-type-live-now{
	color: #bb0000;
	border: 1px solid #bb0000 !important;
}
.badge-style-type-live-now.ytd-badge-supported-renderer{
	border: 1px solid #bb0000 !important;
}

/* Live Badge */
.ytp-live-badge.ytp-button::before{
	background: #bb0000 !important;
}

/* Video Settings HD Icon */
.ytp-red2 .ytp-settings-button.ytp-hd-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-8k-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-3d-badge-grey:after, .ytp-red2 .ytp-settings-button.ytp-3d-badge:after{
	background-color: #bb0000 !important;
}

/* Alert Render */
.INFO.ytd-alert-renderer {
    background-color: #bb0000 !important;
}

/* Top Progress Bar */
#progress.yt-page-navigation-progress{
	background-color: #bb0000 !important;
}

/* Corner Logo */
#logo-icon-container .style-scope .ytd-topbar-logo-renderer .style-scope.ytd-topbar-logo-renderer:nth-child(1){
	fill: #bb0000 !important;
}
#logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer {
    fill: var(--yt-swatch-logo-override, #282828) !important;
}

/* Subscribe Button */
ytd-subscribe-button-renderer:not([button-style]) paper-button:not([subscribed]) {
    background-color: #bb0000 !important;
}

/* Watch Card */
#watch-card-header.ytd-generic-watch-card {
    background: #bb0000 !important;
}
#img-endpoint.ytd-watch-card-collage-renderer #overlay-button.ytd-watch-card-collage-renderer {
    background: #bb0000 !important;
}
paper-tab.iron-selected.ytd-generic-watch-card {
    color: #bb0000 !important;
}
#selectionBar{ background: #bb0000 !important }

/* Brand Color */
*:not([style-scope]):not(.style-scope) {
	--yt-brand-paper-button-color: #bb0000 !important;
	--yt-brand-color: 1px solid #bb0000 !important;
	--paper-tabs-selection-bar-color: #bb0000 !important;
	--yt-spec-brand-button-background: #bb0000 !important;
}
}
