style.css:
@-moz-document domain(youtube.com) {
/* general background-color */
ytd-account-settings,
ytd-active-account-header-renderer,
ytd-alert-renderer,
ytd-browse,
ytd-guide-renderer,
ytd-multi-page-menu-renderer,
ytd-playlist-panel-renderer div,
ytd-playlist-sidebar-renderer,
ytd-search,
ytd-section-list-renderer,
ytd-watch,
#header,
#channel-container div,
#tabs-inner-container, 
.gstl_50 div{
    background-color: #252525 !important;
}

/* banner background color*/
paper-button,
#masthead-container #container,
#end #buttons {
    background-color: #353535 !important;
}

/* mark active channel links */
paper-tab.iron-selected > paper-ripple {
    border-bottom: thin solid #FFFFFF;
}

/* font-color */
a,
div,
input,
h1,
h2,
form,
span,
yt-formatted-string {
    color: #FFFFFF !important;
}

/* search bar on top */
#search-input {
    z-index: 99;
}

/* font-color for SVGs with dark background */
path {
    fill: #FFFFFF !important;
}

/* font-color for SVGs with white background */
#left-arrow path,
#right-arrow path,
#search-icon-legacy path {
    fill: #252525 !important;
}

/* youtube logo -> red */
#logo-icon > svg > g > g > path:first-child {
    fill: #FF0000 !important;
}
}
