style.css:
@-moz-document domain("imoutosite.wordpress.com") {
/*
* Author: Ew0345
* Version: 1.4
*/
.hfeed,
div.entry-content,
div.entry-header,
article,
div#page.site,
p,
span {
    color: #eee !important;
    background-color: #1f1f1f !important;
    /*text-decorate: none !important;*/
    font-family: 'Open Sans', Helvetica, sans-serif !important;
    font-style: normal !important;
}
.site-title a {
    color: #eee !important;
    font-style: italic !important;
}
#actionbar,
.actnbr-action.actnbr-actn-follow,
.actnbr-action.actnbr-actn-follow span,
.sharedaddy.sd-sharing-enabled {
    display: none !important;
    background-color: #232323 !important;
    border-color: #232323 !important;
}
.entry-content-wrapper {
    background-color: #141414 !important;
}
.entry-content,
article,
#page.site,
p,
span {
    font-size: 17px !important;
}
.entry-content {
    padding-left: 2% !important;
    padding-right: 2% !important;
}
.blog-info,
.blog-credits {
    display: none !important;
}
#menu-primary li a {
    color: #aaa !important;
}
#menu-primary .sub-menu li,
#menu-primary .sub-menu li a:link {
    background-color: #303030 !important;
}
#menu-primary .sub-menu li a:hover {
    background-color: #505050 !important;
}
#menu-primary li a:hover{
    font-style: italic !important;
}
}

@-moz-document url("https://imoutosite.wordpress.com/wiseman-pupil-toc/") {
a:visited {
    color: lightblue !important;
}
/* toc pages for visited link coloring */
}
