style.css:
@-moz-document domain("habitica.com") {
.navbar {
    background: #F4F7F7 !important;
  }
  .nav-item:not(.dropdown) .nav-link,.small-text,.dropdown-menu .dropdown-item,.topbar-item,.topbar-item a,.dropdown-title,.topbar-dropdown-item,.topbar-item.droppable a {
    color: #3E5555 !important;
    font-weight: normal !important;
  }
  .navbar .item-with-icon,.reward .small-text {
    color: #3E5555 !important; /* gem is 428bca, gold is 946e00, silver is 999... in case I ever figure out how to differentiate... */
  }
  .navbar .item-with-icon span {
    font-weight: normal !important;
  }
  .item-notifications,.item-user,.navbar .svg-icon {
    fill: #3E5555 !important;
  }
  .message-count.top-count,#nprogress .bar {
	  background-color: #428BCA !important;
  }
  #app-header {
    background: #C5D3D6 !important;
    padding-left: 0px !important;
    padding-bottom: 0px !important;
    padding-top: 0px !important;
  }
  .member-details:not(.row) {
    background: #CFDBDE !important;
  }
  .member-details [class=""] {
    background-color: #DAE3E5 !important;
  }
  .progress {
    background-color: #FFF !important;
    border: 1px solid #FFFFFF !important;
    outline: #999999 solid 1px !important;
  }
  .no-party,.class-badge {
    display: none !important;
  }
  .dropdown-menu:not(.user-dropdown),.dropdown-menu .dropdown-item:hover,.nav-item:hover .nav-link,.topbar-item:hover,.topbar-dropdown-item:hover,.dropdown-item.active {
    background: #E9EFEF !important;
  }
  .dropdown-menu .dropdown-item,.topbar-dropdown {
    background-color: #FFFFFF !important;
  }
  .bg-health {
    background: #DA5353 !important;
  }
  .bg-experience {
    background: #FFCC35 !important;
  }
  .bg-mana {
    background: #4781E7 !important;
  }
  .btn.btn-secondary.pause-button {
    background-color: #47A447 !important;
  }
.chat-row .ml-auto .action .svg-icon {
    display: none !important;
  }
.action.active ~ .action.liked,.action.active {
    background-color: #47A447 !important;
}
.action.liked,.chat-row .ml-auto .action {
    background-color: #A5A1AC;
    color: white;
    padding-right: 5px;
    padding-left: 5px;
    border-radius: 2px;
    cursor: pointer !important;
  }
  .task-disabled-habit-control-bg {
    display: none !important;
  }
  .task-reward-control-bg {
    background: #d0e0e3 !important;
  }
  .task-reward-control-bg:hover {
    background: #fce5cd !important;
  }
  .task-neutral-control-bg {
	  background: #FFCF42 !important;
  }
  .task-neutral-control-inner-daily-todo,.task-good-control-inner-daily-todo,.task-better-control-inner-daily-todo,.task-best-control-inner-daily-todo,.task-bad-control-inner-daily-todo,.task-worse-control-inner-daily-todo,.task-worst-control-inner-daily-todo,.task-disabled-daily-todo-control-inner {
      opacity: .2 !important;
  }
  .task-disabled-daily-todo-control-checkbox,.task-neutral-control-checkbox,.task-good-control-checkbox,.task-better-control-checkbox,.task-best-control-checkbox,.task-bad-control-checkbox,.task-worse-control-checkbox,.task-worst-control-checkbox {
      fill: #000 !important;
  }
  .task-neutral-control-inner-habit,.task-neutral-control-bg:hover .habit-control {
  	background: #ffc314 !important;
  }
  .task-neutral-control-bg ~ .task-content, .task-neutral ~ .task-habit-disabled {
      background: #fff2cc !important;
  }
  .task-good-control-bg {
	  background: #8bbf79 !important;
  }
  .task-good-control-inner-habit,.task-good-control-bg:hover .habit-control {
	  background: #71b05b !important;
  }
  .task-good-control-bg ~ .task-content {
      background: #d9ead3 !important;
  }
  .task-better-control-bg {
	  background: #7eaab2 !important;
  }
  .task-better-control-inner-habit,.task-better-control-bg:hover .habit-control {
	  background: #6398a2 !important;
  }
  .task-better-control-bg ~ .task-content {
      background: #d0e0e3 !important;
  }
  .task-best-control-bg {
	  background: #5288e9 !important;
  }
  .task-best-control-inner-habit,.task-best-control-bg:hover .habit-control {
  	background: #2a6de3 !important;
  }
  .task-best-control-bg ~ .task-content {
      background: #c9daf8 !important;
  }
  .task-bad-control-bg {
	  background: #F4a24c !important;
  }
  .task-bad-control-inner-habit,.task-bad-control-bg:hover .habit-control {
	  background: #f28b21 !important;
  }
  .task-bad-control-bg ~ .task-content {
      background: #fce5cd !important;
  }
  .task-worse-control-bg {
	  background: #dc5d5d !important;
  }
  .task-worse-control-inner-habit,.task-worse-control-bg:hover .habit-control {
	  background: #d43939 !important;
  }
  .task-worse-control-bg ~ .task-content {
      background: #f4cccc !important;
  }
  .task-worst-control-bg {
	  background: #c96652 !important;
  }
  .task-worst-control-inner-habit,.task-worst-control-bg:hover .habit-control {
	  background: #b94f3a !important;
  }
  .task-worst-control-bg ~ .task-content {
      background: #e6b8af !important;
  }
  .task-disabled-daily-todo-control-bg {
	  background: #989898 !important;
  }
  .task-daily-todo-disabled ~ .task-content,.task-disabled-daily-todo-control-content {
      background: #d9d9d9 !important;
  }
  .leader,.profile-name {
    background-color: #999999;
    color: #FFF !important;
    width: auto !important;
    text-indent: 8px;
    border-radius: 6px;
    padding-right: 8px;
  }
  .tier1 .svg-icon,.tier2 .svg-icon,.tier3 .svg-icon,.tier4 .svg-icon,.tier5 .svg-icon,.tier6 .svg-icon,.tier7 .svg-icon,.tier10 .svg-icon,.leader .svg-icon:empty,.tier1 ~ .svg-icon,.tier2 ~ .svg-icon,.tier3 ~ .svg-icon,.tier4 ~ .svg-icon,.tier5 ~ .svg-icon,.tier6 ~ .svg-icon,.tier7 ~ .svg-icon,.tier10 ~ .svg-icon {
    display: none !important
 }
.tier8 .svg-icon,.tier9 .svg-icon {
    margin-left: 0.15em !important;
}
  .tier8 .svg-icon g,.tier9 .svg-icon g,.staff .svg-icon g,.moderator .svg-icon g {
    fill: #FFF !important;
 }
  .tier1 {
    background-color: #F57A9D !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #660823,1px -1px 1px #660823,-1px 1px 1px #660823,1px 1px 1px #660823 !important;
  }
  .tier2 {
    background-color: #B93030 !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #380E0E,1px -1px 1px #380E0E,-1px 1px 1px #380E0E,1px 1px 1px #380E0E !important;
  }
  .tier3 {
    background-color: #F30 !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #4D0F00,1px -1px 1px #4D0F00,-1px 1px 1px #4D0F00,1px 1px 1px #4D0F00 !important;
  }
  .tier4 {
    background-color: #FF9500 !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #4D2D00,1px -1px 1px #4D2D00,-1px 1px 1px #4D2D00,1px 1px 1px #4D2D00 !important;
  }
  .tier5 {
    background-color: #FFF700 !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #4D4A00,1px -1px 1px #4D4A00,-1px 1px 1px #4D4A00,1px 1px 1px #4D4A00 !important;
  }
  .tier6 {
    background-color: #5EFF00 !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #1C4D00,1px -1px 1px #1C4D00,-1px 1px 1px #1C4D00,1px 1px 1px #1C4D00 !important;
  }
  .tier7 {
    background-color: #0AF !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #00334D,1px -1px 1px #00334D,-1px 1px 1px #00334D,1px 1px 1px #00334D !important;
  }
  .moderator,.tier8,.section-body .moderator {
    background-color: #130EAD !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #060434,1px -1px 1px #060434,-1px 1px 1px #060434,1px 1px 1px #060434 !important;}
  .staff,.tier-list .staff,.tier9,.leader .tier9,.section-body .staff:not(.moderator) {
    background-color: #88108F !important;
    color: #FFF !important;
    text-shadow: -1px -1px 1px #29052B,1px -1px 1px #29052B,-1px 1px 1px #29052B,1px 1px 1px #29052B !important;
  }
  .tier-list .npc,.tier10,.col-4.staff.moderator.bailey {
    background-color: #000 !important;
    color: #0F0 !important;
    text-shadow: -1px -1px 1px #000,1px -1px 1px #000,-1px 1px 1px #000,1px 1px 1px #000 !important;
  }
  .row.chat-row .avatar {
    display: none !important;
  }
  .row.chat-row .card {
    max-width: 100% !important;
    width: 100% !important;
    flex: 100% !important;
  }
  .staff .title,.moderator .title {
    color: #FFF !important;
    text-shadow: -1px -1px 1px #000,1px -1px 1px #000,-1px 1px 1px #000,1px 1px 1px #000 !important;
  }
  code {
	  color: #c7254e;
	  background-color: #f9f2f4;
  }
  .navbar-header .logo,.navbar-header .gryphon,.navbar-brand {
    display: none !important;
  }
  .navbar-header {
    margin-right: 0px !important;
  }
  .tasks-column-title div.badge {
    display: none;
  }
  .notification,.notification .icon {
    border-radius: 0px !important;
  }
  .grey-progress-bar {
    border: 1px solid #FFFFFF !important;
    outline: #999999 solid 1px !important;
  }
  .boss-health-bar {
    height: 13px !important;
  }
  .world-boss .corner-decoration,.world-boss .mt-2 {
    display: none !important;
  }
  .world-boss,.world-boss .boss-gradient {
    background: none !important;
    outline: none !important;
    border: none !important;
  }
  .world-boss .float-left,.world-boss .pt-1,.world-boss .mt-3 {
    color: #4E4A57 !important;
  }
  .mentioned-icon ~ .card-body {
    background-color: #EEE !important;
  }
  .mentioned-icon {
    display: none !important;
  }
  .tasks-column .badge-pill {
    display: none !important;
  }
#loading-screen-inapp {
    background: #fff !important;
}
#loading-screen-inapp svg path {
    fill: #432874 !important;
}
#loading-screen-inapp h2,#loading-screen-inapp p {
    font-weight: normal !important;
    color: #333 !important;
}
#loading-screen-inapp p {
    width: 70% !important;
}
.notification {
    background-color: #D9EDF7 !important;
    color: #3A87AD !important;
}
.drop {
    background-color: #D9EAD3 !important;
    color: #415838 !important;
}
.icon-item,.notification .icon {
    color: #786938 !important;
    background-color: rgba(255,255,255,0) !important;
    box-shadow: none !important;
}
.notification .icon {
    flex: 30% !important;
    max-width: 30% !important;
}
.error,.notification .negative,.hp .icon {
    background-color: #F2DEDE !important;
    color: #B94A48 !important;
}
.xp .icon {
    color: #635673 !important;
    background-color: #DCC0FB !important;
}
.mp .icon {
    color: #003AA1 !important;
    background-color: #C7D3E9 !important;
}
.gp .icon {
    background-color: #FBEFC0 !important;
    color: #786938 !important;
}
.notification .info {
    background-color: #F5DEB3 !important;
    color: #A0522D !important;
}
.notification .text {
    text-align: left !important;
    vertical-align: middle !important;
    padding: .5em .5em !important;
    padding-left: 1.5em !important;
}
/*ICONS*/
  .reward-control svg,.item-with-icon.gold svg,.item-with-icon .gem svg,.currency-tray .item-with-icon .svg-icon svg,.svg-icon.gem svg,.challenge-prize svg,.gem-deck .mx-auto svg,.notification.gp svg,.notification.streak svg {
  display: none !important;
  }
.currency-tray > div:nth-child(1) > div:nth-child(1):not(.gem) {
	background-image: url(https://i.imgur.com/gXx5PPe.png) !important;
	background-position: 0px 0px !important;
	background-repeat: no-repeat !important;
	height: 25px;
  background-size: 23px !important;
    overflow: visible !important;
    image-rendering: -webkit-optimize-contrast;
	image-rendering: -moz-crisp-edges;
	image-rendering: -o-pixelated;
	image-rendering: pixelated;
	}
.currency-tray > div:nth-child(1) > div:nth-child(1):not(.gem) ~ span {
    color: #999 !important;
}
.currency-tray .item-with-icon.gold,.reward-control .svg-icon {
	background-image: url(https://i.imgur.com/43r3Dhy.png) !important;
	background-position: 12px 0px !important;
	background-repeat: no-repeat !important;
	height: 22px;
  background-size: 32px !important;
	}
.currency-tray .item-with-icon.gold span {
    color: #946e00 !important;
}
.reward-control .svg-icon {
	background-image: url(https://i.imgur.com/43r3Dhy.png) !important;
	background-position: -1px 0px !important;
	background-repeat: no-repeat !important;
	height: 22px;
  background-size: 28px !important;
	}
.notification.gp .svg-icon,.notification.streak .svg-icon {
    background-image: url(https://i.imgur.com/43r3Dhy.png) !important;
	background-position: -1px 4px !important;
	background-repeat: no-repeat !important;
	height: 23px;
    overflow: visible !important;
    vertical-align: middle !important;
  background-size: 23px !important;
}
.currency-tray .item-with-icon .gem,.svg-icon.gem,.challenge-prize .svg-icon,.gem-deck .mx-auto {
	background-image: url(https://i.imgur.com/jO8l4gE.png) !important;
	background-position: 0px 0px !important;
	background-repeat: no-repeat !important;
	height: 26px !important;
    width: 30px !important;
	margin-left: -18px;
    background-size: 30px !important;
	}
.currency-tray .gem ~ span {
    color: #428bca !important;
}
.purchase-background .gem,.gem-lock .gem {
    background-image: url(https://i.imgur.com/9KdI6oM.png) !important;
	background-position: 0px 0px !important;
    overflow: visible !important;
	background-repeat: no-repeat !important;
	height: 13px !important;
    width: 15px !important;
    background-size: 15px !important;
}
}
@-moz-document url-prefix("https://habitica.com/challenges") {
.col-md-6:not(.tasks-column) {
	  flex: 100%;
	  max-width: 100%;
  }
  .col-md-6:not(.tasks-column) .challenge {
    height: auto !important;
  }
  .challenge-categories,.challenge .well-wrapper,.challenge-description {
    display: none !important;
  }
}
