style.css:
@-moz-document domain("www.zhihu.com") {
.Topstory-container {
	width: 85%;
}

.Topstory-mainColumn{
	width: 85%;
}
.TopstorySideBar{
	width: 25%;
}
.TopstoryMain{
	width: 100%;
}
.Question-main{
	width: 85%;
}
.Question-mainColumn{
	width: 85%;
}
}

@-moz-document domain("zhuanlan.zhihu.com") {
.Layout-main{
	width: 85%;
}
.TitleImage-imagePure{
    width: inherit;
}
}

@-moz-document regexp(".*\\.zhihu.com/search\\?.*") {
.Search-container{
	width: 85%;
}
/*搜索主体*/
.SearchMain{
	width: 85%;
}
/*顶部的相关用户和话题*/
.SearchSections{
	width: 100%;
}
}

@-moz-document regexp(".*\\.zhihu.com/people/.*") {
.Profile-main{
	width: 85%;
}
.Profile-mainColumn{
	width: 100%;
}
}
