style.css:
@-moz-document domain('wiki.tcl.tk') {
	div#content {
    	max-width: 72em;
    	padding: 2em 6em;
  	}

	div#content pre {
		padding:0.5em 0.5em;
		background-color:#efe;
    	border-radius:3px;
    	line-height:1.5;
	}

	div#content hr {
		border: 1px solid black;
    	margin:2em 0;
	}
}
