style.css:
@-moz-document domain(globo.com) {
    body {
    background: #0f0b06 !important
    }
    .diretorio-home {
        background-color: #241b0f;
    }
    .home-topglobocom {
        background-color: #241b0f;
    }
    .hui-section-title, .materia .materia__header .materia__titulo{
        color: #a6a6a6
    }    
    .topglobocom__content-title:before {
        color: #a6a6a6
    }
    body.multicontent {
        color: #bfbfbf
    }
    h1.content-head__title, #glb-materia .materia-titulo h1{
        color: #bfbfbf
    }
    h2.content-head__subtitle, .materia .materia__content .foto .foto-legenda{
        color: #a6a6a6
    }
    #glb-materia .materia-conteudo p, .materia .materia__wrap-content p {
        color: #a6a6a6
    }
    .glbComentarios-dados-usuario>.glbComentarios-texto-comentario, .glbComentarios, .corpo {
        color: #a6a6a6;
    }
    .cb-post-content, .cb-post-content blockquote {
        color: #cac9c9;
    }
  .glbComentarios-dados-usuario>.glbComentarios-dados-usuario-nome {
        color: #cac9c9;
   }
}
