style.css:
@-moz-document domain("mega.nz") {
:root {
	--backgrounds: #050505;
}

body {
	filter: invert(100%);
}

.download .buttons-block > [class*="big-"],
[class*="medium-"],
[class*="small-"],
[class*="*"] {
	filter: invert(100%);
}

.to-clouddrive,
.small-red-cross {
	filter: invert(0%) !important;
}

.download.mid-button.red,
.nw-fm-left-icons-panel,
.cloud-drive,
.nw-fm-left-border,
.fm-avatar,
.activity-status.top,
.tiny-icon.membership-status,
.fm-account-avatar .avatar-wrapper img,
.plan-info > span,
.loading-main-block,
.logo,
.st-bottom-button.st-twitter-button,
.st-bottom-button,
.thumb,
.viewer-image-bl,
.link-button.red,
.fm-dialog-overlay,
.duplicate-conflict.red-header,
.red,
a.red,
a.red:link,
a.red:visited,
a.red:hover,
a.red:active,
.dark-overlay {
	filter: invert(100%) !important;
}

html,
.loading-mid-white-block,
.loading-percentage,
.viewer-image-bl {
	background-color: var(--backgrounds) !important;
}
}
