style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikia.com"), domain("fandom.com") {

/*[[theme]]*/

::-moz-selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}
::selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}

.WikiaPage {
  background-color: var(--bg) !important } /* rgba(0, 0, 0, 0.5) !important } */
body, .WikiaPage .WikiaPageBackground, header {
  background-color: var(--bg) !important }
.WikiaArticle *[style*="background-color"], .wds-global-footer, .wds-global-navigation, .wds-global-navigation  *, #searchInput, .wds-global-navigation__search-input, .wds-global-navigation__search-input:focus, .wds-dropdown__content, .article-categories, .WikiaRail .module, .modal, .module, .modal > footer, .desktop-view, .hot-block, .topic_explore-wikis, .trending-topics, .card__info, .wds-banner-notification__container, .WikiaArticle th, .WikiaArticle table[style*="background"] th > a:link, .wds-community-header .wds-dropdown__content .wds-list.wds-is-linked li:hover > a, .wds-dropdown__content .wds-list.wds-is-linked .wds-dropdown-level-2 .wds-dropdown-level-2__content, .WikiaBarWrapper .tools-menu, .discussions .og-container, .discussion-inline-editor .discussion-inline-editor-floating-container, .pop-over-container, .UserProfileMasthead .masthead-info, .wikia-menu-element, .wikia-menu-button ul, .popover, .card.is-social .card__social-card-background, .top-community .top-community-content, .SpeechBubble .speech-bubble-message, .modalWrapper, .skin-oasis.MiniEditor .cke_dialog_footer, .skin-oasis.MiniEditor .cke_dialog_title, .global-navigation-mobile-wrapper.is-f2 .global-navigation-mobile, .global-navigation-mobile__menu-panel, .WikiaArticle select > option, #tooltip-wrapper, .WikiaArticle .wikia-slideshow-image-caption {
  background-color: var(--bg-2) !important }
oddrow, .wds-banner-notification__container, .UserProfileMasthead .links .zero .icon, .UserProfileMasthead .masthead-info hgroup, .WikiaArticle select > optiongroup {
  background-color: var(--bg-odd) !important }
input, input[type="text"], input[type="password"], textarea {
  background-color: var(--bg-input) !important;
  color: var(--fg-input) !important }
input:focus, input[type="text"]:focus, input[type="password"]:focus select:focus, textarea:focus {
  background-color: var(--bg-input-hover) !important;
  color: var(--fg-input-hover) !important }
.floating-label, .WikiaArticle *[style*="background"], .mainpage .wikia-slideshow-images-wrapper.accent, .WikiaArticle .t-table .row, .t-table .row2, .portable-infobox .pi-title {
  background-color: rgba(255, 255, 255, 0.05) !important }
.hero-block .hero-anchor, section .description, .WikiaBarWrapper, .wds-spinner__overlay {
  background-color: rgba(0, 0, 0, 0.5) !important }
div, .wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::after, .wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::before, .wds-button *, section, .WikiaArticle td, a.wikia-button span, .WikiaArticle *, body .background-image-gradient, .WikiaArticle div[style*="background:transparent"] {
  background-color: transparent !important }
.dropdownmenu li:hover, .wikia-menu-button.secondary ul a:hover, .WikiaBarWrapper .tools-menu > li > a:hover, .wikia-menu-button ul a:hover, .pagination a.active, .discussion-categories-list-content label:hover, .active-element-background-color, .pollAnswerVotes div, .WikiaArticle select > option:checked {
  background-color: var(--fg-link) !important;
  color: var(--bg) !important }
.featured-video__details {
  background-color: #000 !important }
nobg, body .background-image-gradient::before, body .background-image-gradient::after, .article-categories, .hot-block, .trending-topics, .MyToolsConfiguration .popular-tools-group, .WikiaUserPagesHeader, .UserProfileMasthead .masthead-info hgroup, .card.is-social .card__social-card-background, .WikiaArticle table td[style*="background:"], .WikiaSearch input[type="text"] {
  background-image: none !important }

.mainpage .WikiaArticleInterlang {
  background: url("http://images.wikia.com/eswikia/images/5/55/W-Intl_65x65.png") no-repeat }

.wds-dropdown__content .wds-list.wds-is-linked > li > a {
  transition-property: none !important }

body, .wds-global-navigation__wikis-menu, .wds-global-navigation span, div, p, li, .topic-tags a:link, .topic-tags a:visited, table.wikitable, .WikiaArticle font[color], .WikiaArticle span, .post-details-link:link, .WikiaArticle table[style], .wds-button span, .Tooltip .Data, *|*:link, .ibox-hori {
  color: var(--fg) !important }
#header, h1, h2, h3, h4, h5, h6, .dark_text_2, span[style*="color"], .WikiaArticle th, legend, .discussions .og-title, .entry-title > a:link, .top-community-name {
  color: var(--fg-2) !important }
.muted, .wds-global-navigation__search-input::placeholder, .autocomment, .subtle, .wds-banner-notification__text, table.wallfeed td time, .activityfeed .subtle, .post-list .author-attribution .posted-time, .top-community-topic {
  color: var(--fg-muted) !important }

a, a:link, .auth-header .header-callout-emphasis, .top-community-link, .discussion-forum-tabs li.not-active, .wds-global-navigation__link:visited {
  color: var(--fg-link) !important }
a:hover, a:visited:hover, .auth-header *:hover > .header-callout-emphasis, .is-hover .trending-topics__name, .topic-tags a:link:hover, .topic-tags a:visited:hover, .other-communities .other-communities-content-details:hover .other-communities-content-community-name, .discussion-forum-tabs li.not-active:hover {
  color: var(--fg-link-hover) !important }
a:visited {
  color: var(--fg-link-visited) !important }

.WikiaBarWrapper .toolbar .tools > li > a {
  color: #fff !important }
form small.error, small.error {
  color: #f33 !important }
.wds-community-header .wds-tabs__tab-label, .wds-community-header .wds-tabs__tab-label a, .wds-community-header .wds-community-header__counter, .wds-community-header .wds-community-header__sitename a {
  color: unset !important }


.wds-icon, .WikiaRail .rail-module h2.has-icon svg {
  fill: var(--fg) !important }
.wds-community-header__local-navigation .wds-dropdown__content .wds-icon, .user-identity-box-edit svg, .top-community .top-community-link .icon-arrow {
  fill: var(--fg-link) !important }
.featured-block__header > svg {
  stroke: var(--fg) !important }

noborder, pre code, div.ajax-poll, #searchInput, .wds-global-navigation__search-input:focus, .discussions .og-container, .discussions .large-image-card-desktop .og-image, .post-image-editor-tools-wrapper .post-image-inner-image, .discussion .post-actions, .top-community .top-community-content {
  border: none !important }
custom-border, input, textarea, pre, code, .category-gallery-item {
  border: var(--border) !important }
.WikiaArticle table td, .pagination a.active {
  border: solid 0px transparent !important }
bordercolor, hr, h1, h2, h3, h4, h5, h6, hr, .WikiaPage, .WikiaArticle *[style*="border"], .article-categories, div, .wds-global-navigation__search-input-wrapper, .wds-list.wds-has-lines-between > li, section, li, .WikiaArticle hr, .desktop .auth-header::after, span, svg, ul, .WikiaArticle div[style*="border"], .WikiaArticle th, .WikiaArticle td, .mainpage .WikiaArticleInterlang, ul.tabbernav li a, .WikiaArticle table, .WikiaArticle *[style*="border"], .other-communities .other-communities-content-details, .portable-infobox, .SpeechBubble .speech-bubble-message {
  border-color: var(--border-color) !important }
.WikiaArticle .toc, .category-gallery-item img, .WikiaFooter .toolbar .tools .arrow-icon, .WikiaBarWrapper .toolbar .tools .arrow-icon, table.jquery-tablesorter.sortable th.headerSort .chevron, .active-element-border-theme-color {
  border-color: transparent !important }
.discussion-sort .sort-options .sort-option .sort-option-label .radio-fancy {
  border-color: var(--fg) !important }
.discussion-sort .sort-options .sort-option .sort-option-label .radio-fancy .inside {
  border: solid 1px var(--fg-link) !important }
.active-element-background-color {
  border: solid 0.5px #000 !important }
.wds-dropdown::after, .popover.bottom .arrow {
  border-bottom-color: var(--bg-2) !important }
.wds-dropdown::before, .WikiaFooter .toolbar .tools .arrow-icon, .WikiaBarWrapper .toolbar .tools .arrow-icon {
  border-bottom-color: var(--fg-2) !important }
ul.tabbernav li a {
  border-bottom-color: var(--bg) !important }
.wikia-paginator ul .paginator-next::after, .skin-oasis .wikia-slideshow-prev-next .wikia-slideshow-next span .chevron {
  border-left-color: var(--fg-link) }
.wikia-paginator ul .paginator-prev::after, .skin-oasis .wikia-slideshow-prev-next .wikia-slideshow-prev span .chevron {
  border-right-color: var(--fg-link) }
.UserProfileMasthead .masthead-info-lower::before {
  border-top-color: var(--bg-2) !important; border-right-color: var(--bg-2) !important }
.SpeechBubble .speech-bubble-message::after {
  border-bottom-coloR: var(--bg-2) !important; border-right-color: var(--bg-2) !important }
table.jquery-tablesorter.sortable th.headerSort .chevron:last-child {
  border-top-color: var(--fg-link) !important }
table.jquery-tablesorter.sortable th.headerSort .chevron:first-child, .discussion-forum-tabs li.not-active {
  border-bottom-color: var(--fg-link) !important }
hr {
  color: var(--border-color) !important }
.dropdown-menu .divider, hr, .desktop .auth-header::after, .article-attribution::before, .contributor-promo::before {
  background-color: var(--border-color) !important }
input:focus, select:focus, textarea:focus, #loginUsername:focus, #loginPassword:focus, #signupForm input:focus {
  border-color: var(--fg-link) !important;
  box-shadow: 0 0 5px var(--fg-link) !important }
.WikiaArticle .table-is-wide::before {
  box-shadow: -15px 0 20px var(--bg) inset !important }

noshadow, #signupForm input, .input, .category-gallery-item img, #searchInput, .wds-global-navigation__search-input:focus, .hero-block .hero-anchor, .category-gallery-item .snippet, .popover-inner, .ajax-poll {
  box-shadow: none !important }
baseshadow, .wds-global-navigation, .wds-dropdown__content, .WikiaArticle .toc, .category-gallery-item, .wikia-menu-button ul {
  box-shadow: 0 0 5px #000 !important }
.modal > footer, .wds-banner-notification__container, .WikiaPage .WikiaPageBackground, .wds-global-footer, .discussion .post-detail, aside, .popover, .top-community .top-community-content, .modalWrapper, .global-navigation-mobile-wrapper.is-f2 .global-navigation-mobile, .global-navigation-mobile__menu-panel {
  box-shadow: 0 0 10px #000 !important }

a.topic-tags__tag, .pollAnswerVotes span {
  text-shadow: 1px 1px 3px #000 !important }

disabled, input[readonly] {
  background-color: var(--bg-disabled);
  color: var(--fg-disabled) }

.btn, .wds-button, button, button.secondary, a[href*="Special:CreateNewWiki"], .activity-feed-more a, .button, a.wikia-button, a.wikia-button.secondary, .wikia-menu-button, .wikia-menu-button.secondary, ul.post-actions li, .load-more-button, input[type="submit"], .tabs .selected a, .pagination a:not(.inactive):not(.active), .skin-oasis .wikia-slideshow-prev-next a span, ul.tabbernav li:not(.tabberactive) a, .find-posts-button {
  color: var(--fg-input) !important;
  background-image: linear-gradient(var(--bg-input-hover), var(--bg-input)) !important;
  border: solid 0.5px var(--border-color) !important }

.btn.active, .wds-button:active, button:active, button.secondary:active, a[href*="Special:CreateNewWiki"]:active, .activity-feed-more a:active, .button:active, a.wikia-button:active, a.wikia-button.secondary:active, .wikia-menu-button.secondary:active, ul.post-actions li:active, .load-more-button:active, input[type="submit"]:active, .wikia-menu-button > a:hover, .pagination a:not(.inactive):not(.active):active, .skin-oasis .wikia-slideshow-prev-next a span:active, ul.tabbernav li:not(.tabberactive) a:active, .find-posts-button:active {
  background-image: linear-gradient(var(--bg-input), var(--bg-input-hover)) !important }

.btn:hover, .btn:focus, .wds-button:hover, .wds-button:focus, button:hover, button:focus, button.secondary:hover, button.secondary:focus, a[href*="Special:CreateNewWiki"]:focus, a[href*="Special:CreateNewWiki"]:hover, .activity-feed-more a:focus, .activity-feed-more a:hover, .button:focus, .button:hover, a.wikia-button:focus, a.wikia-button.secondary:focus, a.wikia-button:hover, a.wikia-button.secondary:hover, .wikia-menu-button.secondary:focus, .wikia-menu-button.secondary:hover, ul.post-actions li:focus, ul.post-actions li:hover, .load-more-button:focus, .load-more-button:hover, input[type="submit"]:focus, input[type="submit"]:hover, .wikia-menu-button > a:hover, .pagination a:not(.inactive):not(.active):focus, .pagination a:not(.inactive):not(.active):hover, .skin-oasis .wikia-slideshow-prev-next a span:focus, .skin-oasis .wikia-slideshow-prev-next a span:hover, ul.tabbernav li:not(.tabberactive) a:focus, ul.tabbernav li:not(.tabberactive) a:hover, .find-posts-button:focus, .find-posts-button:hover {
  background-color: var(--bg-input) !important;
  border-color: var(--fg-link) !important;
  color: var(--fg-input-hover) !important }

#loginUsername, #loginPassword {
  background-color: transparent !important;
  box-shadow: none !important }

.WikiaArticle pre, pre, code {
  background-color: var(--syntax-bg) }
pre, code, .prettyprint, .nocode, .pln { color: var(--syntax-fg) !important }
.kwd, .tag, .pun, .linenums .tag { color: var(--syntax-builtin) !important }
.atn { color: var(--syntax-keyword) !important }
.dec, .typ { color: var(--syntax-function) !important }
.atv, .str { color: var(--syntax-string) !important }
.num, .lit { color: var(--syntax-number) !important }
.com { color: var(--syntax-comment) !important }

#wds-company-logo-fandom, #wds-company-logo-powered-by-wikia, .modal > header .close, img[width="18"][height="18"] {
  background-color: transparent !important;
  filter: invert(100%) hue-rotate(180deg) }

img[src*="MyHome/images/"], img.sprite {
  filter: saturate(250%) contrast(80%) }
.card.has-image img {
  background-color: #f3f3f3 !important }
.card-background__tint {
  background-color: #ff6a64 !important }

.category-gallery-form #category-exhibition-display-new.active div, .category-gallery-form #category-exhibition-display-new div {
  background-color: var(--fg-link) !important }
.category-gallery-form #category-exhibition-display-old div {
  background-color: var(--fg-muted) !important }

.banner-notifications-placeholder {
  display: none !important }

.insights-light .insights-module-item {
  filter: brightness(200%) }

.article-header__title h1 {
  color: #000 !important }

/* Table headers with partial links are ugly, per https://forum.userstyles.org/discussion/68419/adding-more */
.ibox-hori td > b {
  color: var(--fg-2) !important }
.ibox-hori td > b > a:not(:hover), h1 a:link:not(:hover), h2 a:link:not(:hover), h3 a:link:not(:hover), h4 a:link:not(:hover), h5 a:link:not(:hover), h6 a:link:not(:hover), h1 a:visited:not(:hover), h2 a:visited:not(:hover), h3 a:visited:not(:hover), h4 a:visited:not(:hover), h5 a:visited:not(:hover), h6 a:visited:not(:hover) {
  text-decoration: underline;
  color: var(--fg-2) !important }

}
