style.css:
@-moz-document url("https://torrentfreak.com/"), url-prefix("https://disqus.com/embed/comments/?base=default&f=torrentfreak"), domain("torrentfreak.com") {
body.dark, .pbx.comments, .entry-kilo .entry-title-wrapper, .entry-micro .entry-title-wrapper, .entry-nano .entry-title-wrapper, #feature-box .entry-pico, .entry-title, #main{background: #1C1C1C !important;}

.notification-count, .dark .tab-conversation.active > a, .dark .nav-tab > a, #sidebar, .post-content .post-message p, .dark .icon-cog, .dark .icon-export, .dark .label, .share-bar .thread-share-bar-buttons .thread-share__button, .input--text:placeholder-shown, .placeholder, .textarea, .comments-text, .pbx-title, .stats-number, .entry-title, .entry-lead, .entry-summary, .entry, .hentry{color: #AAA !important;}

.unread .notification-icon{color: #444 !important;}

#wrap-page{background: none !important;}

.share-bar .thread-share-bar-buttons .share-twitter, .share-bar .thread-share-bar-buttons .share-facebook, .dark .input--text, .textarea-wrapper, body, html{background: #141414 !important;}

.entry-kilo, .entry-micro, .entry-nano{border-bottom: 2px solid #1C1C1C !important;}

.dark .input--text, .textarea-wrapper{border-color: #777 !important;}

.auth-disqus button, .auth-facebook button, .auth-google button, .auth-twitter button{filter: grayscale(1) !important;}
}
