style.css:
@-moz-document domain("jenkins.io")
{
html {
    -webkit-filter: invert(100%) !important;
}
body {
    background: #272822;
}
img,
iframe {
    -webkit-filter: invert(100%) !important;
}
#header,
#breadcrumbBar {
    background: #FFFFFF;
}
#main-panel {
    background: #D8D7DD;
}
#side-panel {
    background: #D8D7DD;
}
#header a,
#header a:visited,
#breadcrumbBar a,
#breadcrumbBar a:visited {
    color: #06D98D !important;
}
a,
a:link,
a:visited,
.model-link {
    text-decoration: none;
    color: #992610;
}
a:hover,
.model-link:hover {
    color: #06D98D !important;
}
#description {
    color: #06D98D;
}
.inverse {
    color: #000;
}
}
