style.css:
@-moz-document domain("www.dealabs.com") {
    .thread-title {
        color: #e9e9e9;
    }
    .subNav,
    .subNav--light {
        background-color: #272727;
    }
    .thread-price {
        color: #ff5179;
    }
    .height--all-full .text--color-charcoal {
        color: white;
    }
    .text--color-greyShade {
        color: #e0e0e0;
    }
    .height--all-full {
        background: #1e1e1e;
    }
    .threadGrid {
        background: #35373b;
    }
    .mute--text2,
    .thread--expired.thread--type-card .thread-title,
    .thread--expired.thread--type-list .thread-title {
        color: #acb8cb;
    }
    .bg--main,
    .card,
    .thread--type-card,
    .thread--type-list {
        background-color: #35373b;
    }
    .vote-box {
        background-color: #343436;
    }
    .border--color-borderGrey,
    .border--color-greyTint {
        border-color: #696c71;
    }
    .vote-temp--burn,
    .vote-temp--hot {
        color: #fc587e;
    }
    .voteBar {
        background: none;
    }
    .fadeEdge--r::after,
    .overflow--fade::after {
        background: none;
    }
    .subNavMenu-btn {
        border: 1px solid #6c6e71;
        background-color: #383838;
        color: #03a5c1;
    }
    .btn--mode-boxSec,
    .btn--mode-boxSec:active,
    .btn--mode-boxSec:focus,
    .btn--mode-boxSec:hover {
        border: 1px solid #64676b;
        color: #35373b;
        background-color: #cfd3de;
    }
    .bg--em {
        background-color: #2a2b2c;
    }
    .redactor-editor,
    .popover-content {
        color: #4b4b4b;
    }
    .box--all-b {
        color: #4b4b4b;
    }
}
