style.css:
@-moz-document domain("download.net.pl"), domain("www.downloadsource.net"), domain("www.downloadsource.fr"), domain("www.downloadsource.es"), domain("www.downloadsource.com.br") {

 /* ONLY IN ARTICLES POSSIBLE FOUND DATE, NO LONGER ON HOMEPAGE WITHOUT CUSTOM JS */
.row > .text-left > h1 ~ dl > .date + dd.date,
.row > .text-left > h1 ~ dl.sr-only {
    width: auto !important;
    height: auto !important;
    clip: unset !important;
    display: inline-block !important;
}

.row > .text-left > h1 ~ dl > dt.date:lang(pl) {
    display: inline-block !important;
}

/* ONLY POLISH HAVE TRANSLATION IN CMS FOR DT.date TAG */
.row > .text-left > h1 ~ dl > dt.date {
    display: none !important;
}

/* LEGACY
