style.css:
@-moz-document domain("unifi.ubnt.com"), domain("demo.ubnt.com") {
Body {background: #272b30
}

.appActionBar{background-color: #969ba3
}

.appTable--light, .ubntMainPanel, .ubntNavigation, .ubntHeaderPanel--primary{background-color: #33373c
}

.ubntSidePanel--navigation, .unifiPanel {border-color: #272b30
}

.appTable--striped .tbody .tr:nth-child(even):not(.is-row-selected), .appTable--striped .tbody tr:nth-child(even):not(.is-row-selected), .appTable--striped tbody .tr:nth-child(even):not(.is-row-selected), .appTable--striped tbody tr:nth-child(even):not(.is-row-selected){background-color: #616770
}

.appTable--striped .tbody, .appTable--striped tbody{border-bottom: 1px solid #111111
}

input, select, .appFormDropdown__selectedItem, output, textarea{background-color: #454d51
}

.appToggle{background-color: #969ba3
}

.unifiPanel{background-color: #33373c
}

.unifiPanelItemHeader{background-color: #33373c
}

.unifiPanelItemBody{background-color: #33373c
}

.unifiPanelItemContent__section{background-color: #44484e
}




.appTable--hoverable .tbody .tr:hover:not(.is-row-selected), .appTable--hoverable .tbody tr:hover:not(.is-row-selected), .appTable--hoverable tbody .tr:hover:not(.is-row-selected), .appTable--hoverable tbody tr:hover:not(.is-row-selected) {background-color: #71757b !important
}

.row--comfy {background-color: #3c3e42;
             border-radius: 10px;
}

.unifiSmartMarginContainer {margin-top: 15px
}

.unifiLineChart__yAxisLabel {fill: #afafaf
}

.appSubHeading--light {color: #afafaf
}

.appSubHeading--standalone{border-bottom: 1px solid #777777
}

.appTabs--subHeading, .appTabs {border-color: #777
}

.unifiDonutChartExternalRing {fill: #272b30
}

.appTooltip {background-color: #272b30;
             border-color: #3c3e42;
}

.appTooltip--bottom:after, .appTooltip--bottomLeft:after, .appTooltip--bottomRight:after, .appTooltip--bottom:before, .appTooltip--bottomLeft:before, .appTooltip--bottomRight:before {border-bottom-color: #3c3e42
}

.unifiChartTooltip__inner {background-color: rgba(83, 85, 90, 0.9);
                           border: 1px solid #272b30;
                           box-shadow: 0 0 15px rgba(0, 0, 0, 0.25);
}

.withHeavyIcons .unifi-icon--default-close:before, .unifi-icon--default-close:before {content: "\2715"
}

.appGlobalSideNavPopover__inner, .appGlobalSideNavTooltip {background-color: #191c21
}

.appGlobalSideNavPopover:after {border-bottom-color: #191c21
}

.appGlobalSideNavTooltip:after {border-right-color: #191c21
}

.appStatusBall--positive {background-color: #0bbf33
}

.appStatusIcon--positive, .success {color: #0bbf33 !important
}

.appBadge--success {border-color: rgba(11, 191, 51, 0.5);
                    color: #0bbf33;
}
}
