style.css:
@-moz-document domain("magister.net") {
#menuKnopVandaag {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopAgenda {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopAfwezigheid {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopCijfers {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopBerichten {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopELO {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopELO + ul {
    background-color: #ffffff;
    background-image: none;
}
#menuKnopELO + ul * {
    color: #000000;
}
#menuKnopActiviteiten {
    background-color:rgba(0, 0, 0, 0);
}
#menuKnopLeermiddelen {
    background-color: rgba(0, 0, 0, 0);
}
.navTrigger {background-color: #333333;}
.menu ul {
    background-image: linear-gradient(to bottom, #ff0000, #ff8800, #ffff00, #00ff00, #00ffff, #0000ff, #ff00ff);
    background-size: 200% 90%;
    background-position-y: 120%;
}
.menu ul * {border: none;}
#menuKnopVandaag:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopAgenda:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopAfwezigheid:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopCijfers:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopBerichten:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopELO:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopELO + ul > li:hover * {
    color: #888888; 
    background-color: #ffffff;
}
#menuKnopELO + ul > li:hover {box-shadow: 0px 0px 8px #888888;}
#menuKnopELO + ul {
    position: relative;
    top: 0%;
    left: 100%;
}
#menuKnopELO + ul, #menuKnopELO + ul * {border: none;}
#menuKnopActiviteiten:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
#menuKnopLeermiddelen:hover {
    background-color: rgba(255, 255, 255, .7);
    color: #000000;
}
nav.menu > ul > *.ng-scope {
    background-color: rgba(0, 0, 0, 0);
}
}
